const initialState={
    avatarColor:"",
    name:"",
    backGroundImage:""
};

var loginInfoReducer = (state=initialState,action)=>{
    switch(action.type)
    {
        case 'SET_LOGININFO':
            const { name , avatarType, backGroundImage } = action.payload;
            return {
                name:name,
                avatorType:avatarType,
                avatarColor : '',
                backGroundImage:backGroundImage
            }
    }
    return state;
};
