import io from 'socket.io-client';
import { useState ,useEffect} from 'react'
import DressInput from '@mui/material/TextField';
import AvatarIcon from '../components/AvatorIcon';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Checkbox from '@mui/material/Checkbox';
import Avatar from '@mui/material/Avatar';
import store from '../store/store.js';
import '../style/ChatRoom.css'
function ChatRoom() {
    const [messageList,setMessageList] = useState([]);
    const [message, setMessage] = useState('');
    const [socketio, setSocket] = useState();
    console.log(`messagesInRender:${messageList.map(mes=>mes)}`);
    function submitEvent(){
      if (socketio !== undefined){
        //ソケットを通してset_nameイベントを発火する
        const avator = store.getState().loginInfoReducer.avatorType;
        const name = store.getState().loginInfoReducer.name;
        console.log(`アバター:${avator}`);
        socketio.emit('message',{avatorType:avator,name:name,message:message});
        document.getElementById('message').value='';
        setMessage('');
      }
    };
    useEffect(()=>{
      const socketio = io('http://192.168.0.160:8000');
      setSocket(socketio);
      socketio.on('message',(msg)=>{
        setMessageList([...messageList,msg]);
        console.log(messageList);
      });
    },[]);
 
  return (
    <div className="ChatRoom">
      <header className="ChatRoom-header">
       <List dense sx={{ width: '100%', maxWidth: 260, bgcolor:'rgba(255, 255, 255, 0.1)' }}>
      {messageList.map((value,id) => {
        const labelId = `checkbox-list-secondary-label-${value}`;
        return (
          <ListItem
            key={id}>
            <ListItemAvatar>
             <AvatarIcon avatorType={value.avatorType} height="36px" />
            </ListItemAvatar>
            <ListItemText id={labelId} primary={`${value.name} > ${value.message}`}  />
          </ListItem>
        );
      })}
    </List>
        <DressInput label="" variant="standard" className="input--" id="message" type="text" onChange={e => setMessage(e.target.value)} />
        <Button variant="outlined" type="button" onClick={()=>{submitEvent();}}>送信
        </Button>
      </header>
    </div>
  );
}

export default ChatRoom;
