import logo from './logo.svg';
import './App.css';
import io from 'socket.io-client'
import { useState ,useEffect} from 'react'
import DressInput from '@mui/material/TextField';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Checkbox from '@mui/material/Checkbox';
import Avatar from '@mui/material/Avatar';
function App() {
    const [messageList,setMessageList] = useState([]);
    const [name, setName] = useState('');
    const [message, setMessage] = useState('');
    const [socketio, setSocket] = useState();
    console.log(`messagesInRender:${messageList.map(mes=>mes)}`);
    function submitEvent(){
      if (socketio !== undefined){
        //ソケットを通してset_nameイベントを発火する
        socketio.emit('message',{name:name,message:message});

        console.log(name)

      }
    };
    useEffect(()=>{
      const socketio = io('http://192.168.0.160:8000');
      setSocket(socketio);
      socketio.on('message',(msg)=>{
        console.log(`res:${msg}`);
        setMessageList([...messageList,msg]);
        console.log(`messages:${messageList.toString()}`)
      });
    },[]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>SHINOBI</h1>
        <img src={logo} className="App-logo" alt="logo" />
       <DressInput id="outlined-basic" variant="filled" label="忍名" className="input--" type="text" onChange={e => setName(e.target.value)} />
       <List dense sx={{ width: '100%', maxWidth: 260, bgcolor:'rgba(255, 255, 255, 0.1)' }}>
      {messageList.map((value) => {
        const labelId = `checkbox-list-secondary-label-${value}`;
        return (
          <ListItem
            key={value}>
            <ListItemAvatar>
              <Avatar
                alt={`Avatar n°${value.name}`}
                src={`/static/images/avatar/${value.name}.jpg`}
              />
            </ListItemAvatar>
            <ListItemText id={labelId} primary={value.message}  />
          </ListItem>
        );
      })}
    </List>
        <DressInput label="" variant="standard" className="input--" type="text" onChange={e => setMessage(e.target.value)} />
        <Button variant="outlined" type="button" onClick={()=>{submitEvent();}}>送信
        </Button>
      </header>
    </div>
  );
}

export default App;
