import React from 'react'
import avatorJson from '../settings/avator.json';
function AvatorIcon({avatorTypeKey,height}) {
    function getSource(key){
        const avater= avatorJson.avotors.find(x=>x.key==key);
        const avatorType= avater.type;
        import(`../svg/${avatorType}.svg`).then(
            // defaultとして読み込んだモジュールを「MyModule」という名前空間として、
            // then()のコールバック関数の引数に渡す
            ({ default: src }) => {
                console.log(src);
             return src
            }
          ).catch(err => {
            //  エラー処理
          });
    }

    const style = {
        height: {height},       // 数値は"64px"のように、pxとして扱われます
        pointerEvents: 'none',
      };
  return (
    <div className="avatorIcon">
        <img src={getSource(avatorTypeKey)} style={style}  alt="アバター" />
    </div>
  )
}

export default AvatorIcon
