import '../App.css';
import logo from '../svg/gang.svg'
import AvatorIcon from './AvatorIcon';
import { useState ,useEffect} from 'react'
import DressInput from '@mui/material/TextField';
import Button from '@mui/material/Button';
import {useNavigate} from 'react-router-dom';
import {useDispatch} from 'react-redux';
import avatorJson from '../settings/avator.json';

function Login() {
    const [name, setName] = useState('');
    const [avatorTypeKey , setAvatorTypeKey] = useState(1);
    const [backGroundImage , setBackGroundImage] = useState();
    const [avatorType , setAvatorType] = useState('shinobi');
    const navigation = useNavigate();
    const dispatch = useDispatch();
    console.log(`avatorTypeAA:${avatorType}`);
    console.log(backGroundImage);
    function onFileChange(e){
        const files = e.target.files
        if(files.length > 0) {
            var file = files[0]
            var reader = new FileReader()
            reader.onload = (e) => {
                setBackGroundImage( e.target.result)
            };
            
            reader.readAsDataURL(file)
    
        } else {
            setBackGroundImage(null);
        }
    
    }
    //アバターの参照svgを変更
    const ChangeAvatorType = ()=>{
        const nextAvaorTypeKey =avatorTypeKey % avatorJson.avotors.length +1;
        const avater= avatorJson.avotors.find(x=>x.key==nextAvaorTypeKey);
        const avatorType= avater.type;
        setAvatorTypeKey(nextAvaorTypeKey);
        setAvatorType(avatorType);
    }
    console.log(avatorType);
    const Entry = ()=>{
        if(!name || !avatorType){
            return;
        }
        dispatch({
            type:"SET_LOGININFO",
            payload:{
                name:name,
                avatarType:avatorType,
                backGroundImage:backGroundImage
            }
        });
        navigation('/Home');
    };
  return (
    <div className="App">
      <header className="App-header">
        <h1>SHINOBI</h1>
        <button type="button" onClick={()=>{ChangeAvatorType()}} >
         <AvatorIcon avatorType={avatorType} height='20vmin' />
        </button>
        <DressInput id="outlined-basic" variant="filled" label="忍名" className="input--" type="text" onChange={e => setName(e.target.value)} />
        <input type="file" className="input--" onInput={(e)=>{onFileChange(e)}} />
        <Button variant="outlined" type="button" onClick={()=>{Entry();}}>入室</Button>
      </header>
    </div>
  );
}

export default Login;
