import '../App.css';
import {getAvatorType} from '../utils/util';
import { useState ,useEffect} from 'react'
import DressInput from '@mui/material/TextField';
import Button from '@mui/material/Button';
function App() {
    const [name, setName] = useState('');
    const [avatorTypeKey , setAvatorTypeKey] = useState(1);
    const [avatorType , setAvatorType] = useState(getAvatorType(avatorTypeKey));
    //アバターの参照svgを変更
    const ChangeAvatorType = ()=>{
            const nextAvaorTypeKey =avatorTypeKey+1;
            setAvatorType(getAvatorType(nextAvaorTypeKey));
            setAvatorTypeKey(nextAvaorTypeKey);
    }
  return (
    <div className="App">
      <header className="App-header">
        <h1>SHINOBI</h1>
        <button type="button" onClick={()=>{ChangeAvatorType()}} >
         <img src={`../svg/${avatorType}.svg`} className="App-logo" alt="logo" /> 
        </button>
       <DressInput id="outlined-basic" variant="filled" label="忍名" className="input--" type="text" onChange={e => setName(e.target.value)} />
        <Button variant="outlined" type="button" onClick={()=>{submitEvent();}}>入室</Button>
      </header>
    </div>
  );
}

export default App;
